echo "==============================当前工作目录==============================="
workdir=$(cd $(dirname $0); pwd)
echo "$workdir"
echo "==============================关闭集群节点==============================="
for i in {1..9}; do
  redis-cli -p 700"$i" shutdown
  echo "关闭节点{700$i} OK!"
done
echo "===============================集群关闭完成==============================="
echo "see you later..."